import React from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Snackbar from "@material-ui/core/Snackbar";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close"
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogActions from "@material-ui/core/DialogActions";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import {
    MuiPickersUtilsProvider,
    KeyboardTimePicker,
    KeyboardDatePicker,
} from '@material-ui/pickers';
import DateFnsUtils from "@date-io/date-fns";
import Grid from "@material-ui/core/Grid";
import Cards from './Cards'
import cookie from 'react-cookies'

class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            hello: 'world',
            userEmail: cookie.load('email') ?  cookie.load('email') : '',
            userVerified: cookie.load('verified') ? cookie.load('verified') : false,
            cards: [
                {
                    location: 'carmichael',
                    time: '10pm'
                },
                {
                    location: 'carmichael',
                    time: '10pm'
                },
                {
                    location: 'carmichael',
                    time: '10pm'
                },
                {
                    location: 'carmichael',
                    time: '10pm'
                },
                {
                    location: 'hodgdon',
                    time: '10pm'
                }
            ],
            showLogin: false,
            enteredEmail: '',
            enteredCode: '',
            enteredDate: new Date(),
            showLoginMessage: false,
            showInvalidMessage: false,
            showSuccessMessage: false,
            showCodeField: true,
            showGiverForm: false,
            enteredDiningHall: ''
        };
    }


    submitEmail = () => {
        const {enteredEmail} = this.state
        if (this.isValidEmail(enteredEmail)){
            this.setState({
                showSuccessMessage: true
            })
        } else {
            this.setState({
                showInvalidMessage: true
            });
            return false;
        }
        const body = {
            email: enteredEmail
        };
        fetch('https://swipeshareapi.herokuapp.com/sendcode',
            {method:'POST',
                body:JSON.stringify(body),
                headers: {
                'Content-Type': 'application/json'
            }})
            .then(response => response.status)
            .then(status => {
                if (status !== 200){
                    console.log('big bad')
                }
            }).catch(x => {
            console.log('no data', x)
            return('no data')
        })

        const success = true;
        if (success){
            this.setState({showCodeField:true, enteredCode: ''})
        }
    }


    submitCode = () => {
        const {enteredCode, enteredEmail} = this.state
        if (!this.isValidEmail(enteredEmail)){
            this.setState({
                showInvalidMessage: true
            });
            return false;
        }
        const body = {
            email: enteredEmail,
            code: enteredCode
        };
        fetch('https://swipeshareapi.herokuapp.com/login',
            {method:'POST',
                body:JSON.stringify(body),
                headers: {
                    'Content-Type': 'application/json'
                }})
            .then(response => response.status)
            .then(status => {
                if (status !== 200){
                    console.log('big bad')
                } else {
                    this.setState({
                        userEmail: enteredEmail,
                        userVerified: true
                    })
                }
            }).catch(x => {
            console.log('no data', x)
            return('no data')
        })
        console.log(enteredCode)
    };

    isValidEmail = enteredEmail => {
        const length = enteredEmail.length;
        return enteredEmail.substring(length - 10, length) === '@tufts.edu'
    };

    loginWindow(){
        const {enteredEmail, enteredCode, showCodeField} = this.state
        return(
            <div>
                <TextField
                    required
                    id="outlined-email-input"
                    label="Email"
                    type="email"
                    name="email"
                    autoComplete="email"
                    margin="normal"
                    variant="outlined"
                    style={{width: 450}}
                    value={enteredEmail}
                    onChange={e => this.setState({enteredEmail: e.target.value, showInvalidMessage: false})}
                />
                <br/>
                {showCodeField ? (
                    <div>
                        <TextField
                            required
                            id="outlined-email-input"
                            label="Code"
                            name="code"
                            margin="normal"
                            variant="outlined"
                            style={{width: 450}}
                            value={enteredCode}
                            onChange={e => this.setState({enteredCode: e.target.value})}
                        />

                        <div style={{marginLeft:320,  cursor: 'pointer', textDecoration: 'underline'}} onClick={() => this.setState({showCodeField: false})}>Don't have a code?</div>
                    </div>
                ) : null}
                {showCodeField ?
                    <Button type='submit' variant='contained' onClick={this.submitCode}>submit</Button> :
                    <div>
                        <div style={{marginLeft:300,  cursor: 'pointer', textDecoration: 'underline'}} onClick={() => this.setState({showCodeField: true})}>Have a code already?</div>
                        <Button type='submit' variant='contained' onClick={this.submitEmail}>send code</Button>
                    </div>

                }

            </div>
        )
    }

    // componentDidUpdate(prevProps, prevState, snapshot) {
